import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { LoginOutlined,  } from '@ant-design/icons';
import { Menu } from 'antd';

import { Link } from 'react-router-dom'
const items = [
    {
      label: 'Login',
      key: 'login',
      icon: <LoginOutlined />,
      to: '/login'
    },
    {
      label: 'Register',
      key: 'register',
    //   icon: <AppstoreOutlined />,
      to: '/register'
    },
  ];

const Navbar = () => {
    const [current, setCurrent] = useState(''); // ลบค่าเริ่มต้นออก
    const location = useLocation(); // เรียกใช้ hook useLocation

    useEffect(() => {
        // ในทุกครั้งที่ location เปลี่ยนแปลง เช่น URL ที่ถูกเลือกโดย Router จะเปลี่ยน
        // เราจะต้องอัปเดตค่า current state ตาม path ของ URL ที่เปลี่ยนแปลง
        const path = location.pathname;
        setCurrent(path === '/' ? 'login' : path.substring(1)); // ตัดอักขระ '/' แรกออก
    }, [location]); // เรียกใช้ useEffect เมื่อ location เปลี่ยนแปลง

    const onClick = (e) => {
      console.log('click ', e);
      setCurrent(e.key);
    };

    return    <Menu onClick={onClick} selectedKeys={[current]} mode="horizontal">
                {items.map(item => (
                <Menu.Item key={item.key} icon={item.icon}>
                    <Link to={item.to}>{item.label}</Link>
                </Menu.Item>
                ))}
            </Menu>
};
export default Navbar;
